// variables available via "process.env.XYZ" in source files of the project

module.exports = function(pkg = null, mode = process.env.NODE_ENV, targetEnvironment = "browser", outputFile = null) {
  if (pkg == null)
    pkg = require("uu_appg01_devkit-common/src/tools/package.js")
      .getSingletonSync()
      .get();
  return {
    NAME: pkg.name, // library/app name
    VERSION: pkg.version,
    OUTPUT_NAME: outputFile ? outputFile.replace(/(\.min)?\.(js|css)$/, "") : pkg.name,
    NODE_ENV: mode,
    TARGET_ENVIRONMENT: targetEnvironment
  };
};
