"use strict";

const SysAppConfigModeModel = require("../models/sys-app-config-model");

class SysAppConfigController {
  initApp(ucEnv) {
    return SysAppConfigModeModel.initApp(ucEnv.uri.getAwid(), ucEnv.parameters);
  }

  setAppConfig(ucEnv) {
    return SysAppConfigModeModel.setAppConfig(ucEnv.uri.getAwid(), ucEnv.parameters);
  }

  getAppConfig(ucEnv) {
    return SysAppConfigModeModel.getAppConfig(ucEnv.uri.getAwid());
  }
}

module.exports = new SysAppConfigController();
