"use strict";

const SysAppRuntimeModeModel = require("../models/sys-app-runtime-mode-model.js");

class SysAppRuntimeModeController {
  setAppRuntimeMode(ucEnv) {
    return SysAppRuntimeModeModel.setAppRuntimeMode(ucEnv.uri.getAwid(), ucEnv.parameters);
  }

  getAppRuntimeMode(ucEnv) {
    return SysAppRuntimeModeModel.getAppRuntimeMode(ucEnv.uri.getAwid());
  }
}

module.exports = new SysAppRuntimeModeController();
