"use strict";

const SysAppWorkspaceConfigModel = require("../models/sys-app-workspace-config-model.js");

class SysAppWorkspaceConfigController {
  setConfiguration(ucEnv) {
    return SysAppWorkspaceConfigModel.setConfiguration(ucEnv.uri.getAwid(), ucEnv.parameters);
  }

  getConfiguration(ucEnv) {
    return SysAppWorkspaceConfigModel.getConfiguration(ucEnv.uri.getAwid());
  }

  clearConfiguration(ucEnv) {
    return SysAppWorkspaceConfigModel.clearConfiguration(ucEnv.uri.getAwid());
  }
}

module.exports = new SysAppWorkspaceConfigController();
