"use strict";

const SysDataStoreStatsModel = require("../models/sys-data-store-stats-model.js");

class SysDataStoreStatsController {
  getDataStoreStats(ucEnv) {
    return SysDataStoreStatsModel.getDataStoreStats(ucEnv.uri.getAwid(), ucEnv.parameters);
  }

  listDataStoreStats(ucEnv) {
    return SysDataStoreStatsModel.listDataStoreStats(ucEnv.parameters);
  }

  calculateDataStoreStats(ucEnv) {
    return SysDataStoreStatsModel.calculateDataStoreStats(ucEnv.uri.getAwid(), ucEnv.parameters);
  }
}

module.exports = new SysDataStoreStatsController();
