"use strict";

const SysPermissionModel = require("../models/sys-permission-model.js");

class SysPermissionController {
  createPermission(ucEnv) {
    return SysPermissionModel.create(ucEnv.uri.getAwid(), ucEnv.parameters);
  }

  deletePermission(ucEnv) {
    return SysPermissionModel.delete(ucEnv.uri.getAwid(), ucEnv.parameters);
  }

  listPermissions(ucEnv) {
    return SysPermissionModel.listPermissions(ucEnv.uri.getAwid(), ucEnv.parameters);
  }
}

module.exports = new SysPermissionController();
