"use strict";

const SysProfileModel = require("../models/sys-profile-model.js");

class SysProfileController {
  setProfile(ucEnv) {
    return SysProfileModel.setProfile(ucEnv.uri.getAwid(), ucEnv.parameters);
  }

  getProfile(ucEnv) {
    return SysProfileModel.getProfile(ucEnv.uri.getAwid(), ucEnv.parameters);
  }

  listProfiles(ucEnv) {
    return SysProfileModel.listProfiles(ucEnv.uri.getAwid(), ucEnv.parameters);
  }

  updateProfiles(ucEnv) {
    return SysProfileModel.updateProfiles(ucEnv.parameters);
  }
}

module.exports = new SysProfileController();
