"use strict";

const { UuObjectDao } = require("uu_appg01_objectstore");

class SysAppConfigMongo extends UuObjectDao {
  async createSchema() {
    await super.createIndex(
      {
        asid: 1
      },
      { unique: true }
    );
  }

  async create(uuObject) {
    return super.insertOne(uuObject);
  }

  async getByAsid(asid) {
    return super.findOne({ asid });
  }

  async update(uuObject) {
    return super.findOneAndUpdate({ asid: uuObject.asid }, uuObject, "NONE");
  }
}

module.exports = SysAppConfigMongo;
