"use strict";

const { UuObjectDao } = require("uu_appg01_objectstore");

class SysKeyStoreMongo extends UuObjectDao {
  async createSchema() {
    await super.createIndex({ awid: 1, keySet: 1, keyCode: 1 }, { unique: true });
  }

  async create(uuObject) {
    return await super.insertOne(uuObject);
  }

  async getByAwidAndKeySetAndKeyCode(awid, keySet, keyCode) {
    return await super.findOne({ awid, keySet, keyCode });
  }

  async listByAwidAndKeySet(awid, keySet, pageInfo = {}) {
    let filter = { awid, keySet };
    return super.find(filter, pageInfo);
  }

  async update(uuObject) {
    let filter = {
      awid: uuObject.awid,
      keySet: uuObject.keySet,
      keyCode: uuObject.keyCode
    };
    return await super.findOneAndUpdate(filter, uuObject);
  }
}

module.exports = SysKeyStoreMongo;
