const { BaseError } = require("uu_appg01_core-utils");

class InvalidAppKey extends BaseError {
  constructor(invalidKey) {
    super(`The AppKey (${invalidKey}) is not valid.`, null);
    this.code = "uu-app-workspace/sysAppKey/invalidAppKey";
  }
}

class InconsistentAppKey extends BaseError {
  constructor(partName, partValue) {
    super(
      `The AppKey is not valid. The ${partName} part (${partValue}) is not valid. Null value was expected because some AppKey part before was null.`,
      null
    );
    this.code = "uu-app-workspace/sysAppKey/inconsistentAppKey";
  }
}

class InvalidAppKeyPart extends BaseError {
  constructor(partName, partValue) {
    super(`The AppKey is not valid. The ${partName} part (${partValue}) is not valid.`, null);
    this.code = "uu-app-workspace/sysAppKey/invalidAppKeyPart";
  }
}

module.exports = {
  InvalidAppKey,
  InconsistentAppKey,
  InvalidAppKeyPart
};
