const WorkspaceError = require("./workspace-error.js");

const PutKey = {
  UC_CODE: `${WorkspaceError.ERROR_PREFIX}sysKeyStore/`,
  SysKeyStoreDaoCreateFailed: class extends WorkspaceError {
    constructor() {
      super(...arguments);
      this.message = "Create sysKeyStore by sysKeyStore DAO create failed.";
      this.code = `${PutKey.UC_CODE}daoCreateFailed`;
    }
  },
  SysKeyStoreDaoUpdateFailed: class extends WorkspaceError {
    constructor() {
      super(...arguments);
      this.message = "Update sysKeyStore by sysKeyStore DAO create failed.";
      this.code = `${PutKey.UC_CODE}daoUpdateFailed`;
    }
  }
};

module.exports = {
  PutKey
};
