const WorkspaceError = require("./workspace-error.js");

const SysSetProfile = {
  UC_CODE: `${WorkspaceError.ERROR_PREFIX}sysSetProfile/`,

  InvalidDtoInError: class extends WorkspaceError {
    constructor() {
      super(...arguments);
      this.message = "DtoIn is not valid.";
      this.code = `${SysSetProfile.UC_CODE}invalidDtoIn`;
    }
  },

  DaoUpdateByCodeFailedError: class extends WorkspaceError {
    constructor() {
      super(...arguments);
      this.message = "Update sysProfile by sysProfile DAO updateByCode failed.";
      this.code = `${SysSetProfile.UC_CODE}sysProfileDaoUpdateByCodeFailed`;
    }
  }
};

