const { DaoFactory } = require("uu_appg01_objectstore");

class SysImportStartup {
  /**
   * Initialize schema for import mappings on server start.
   */
  onStartup() {
    if (DaoFactory.isDataStoreOn()) {
      const dao = require("../models/sys-dump-restore-model").importMappingDao;
      dao.createSchema();
    }
  }
}

module.exports = SysImportStartup;
