/* eslint-disable */
const sysInitAppWorkspaceDtoInType = shape({
  awid: hexa32Code().isRequired(),
  awidOwner: uuIdentity().isRequired(),
  licenseOwner: shape({
    organization: shape({
      name: uu5String(4000).isRequired(),
      oId: code().isRequired(),
      web: uri()
    }),
    userList: array(
      shape({
        uuIdentity: uuIdentity().isRequired(),
        name: uu5String(4000).isRequired(),
      }), 1, 30
    ).isRequired()
  }).isRequired(),
  runtimeMode: shape({
    mode: oneOf(["standard","privileged"]).isRequired(),
    note: uu5String(),
    to: datetime()
  })
});

const sysGetAppWorkspaceDtoInType = shape({
  awid: id().isRequired()
});

const sysSetAuthTypeDtoInType = shape({
  authorizationStrategy: oneOf(["uuIdentity", "roleGroupInterface", "artifact", "boundArtifact"]).isRequired()
});

const sysConnectArtifactDtoInType = shape({
  artifactUri: uri().isRequired(),
  synchronizeArtifactBasicAttributes: boolean()
});

const sysSetAppWorkspaceStateDtoInType = shape({
  awid: hexa32Code().isRequired(),
  state: oneOf(["initialized", "closed"]).isRequired()
});

const sysDeleteAppWorkspaceDtoInType = shape({
  awid: hexa32Code().isRequired()
});

const sysSetAwidRuntimeModeDtoInType = shape({
  mode: oneOf(["standard","privileged"]).isRequired(),
  note: uu5String(),
  to: datetime()
});
