/* eslint-disable */
const profilesPrivilegedPart = shape({
  profileList: array(code()).isRequired(),
  permissionMap: map(string(/^{.*}$/), array(string())).isRequired(),
  useCaseMap: map(string(), array(string())).isRequired()
});

const profilesType = shape({
  '{tid}-{asid}': profilesPrivilegedPart().isRequired('{asid}'),
  '{asid}': profilesPrivilegedPart(),
  '*': shape({
    profileList: array(code()).isRequired(),
    useCaseMap: map(string(), array(string())).isRequired(),
    privilegedUseCaseMap: map(string(), array(string()))
  }).isRequired()
});

const deployConfigMapType = shape({
  uuSubAppDataStoreMap: shape({}, true).isRequired(),
  privilegedUserMap: shape({}, true).isRequired(),
  tid: string(/^[0-9a-f]{1,32}$/).isRequired(), //TODO this regex supports territory OID, that is not 32 characters long
  asid: code().isRequired() //TODO hexa32code BUG
}); // TODO this schema is not used anywhere
